import React from "react";
import Machine from './machine'
import {compose} from "redux";
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";
import {getNumMachine, updateParameter} from "../../redux/parametersReducer";

const MachineContainer = (props) => {
    let tempNumber = props.match.params.number;
    let currentMachine = props.machine.filter(m => m.number === tempNumber)[0]
    return <Machine currentMachine={currentMachine} updateParameter={props.updateParameter}  />
}


let mapStateToProps = (state) => {
    return {
        machine: state.ParamsPage.machine,
        currentMachine: state.ParamsPage.currentMachine
    }
}

export default compose(
    connect(mapStateToProps, {
        getNumMachine, updateParameter})
    ,withRouter) (MachineContainer);
